import React, { useState } from 'react';

function Checkout(props) {
    const [checkoutInput, setCheckoutInput] = useState({
        firstname: '',
        lastname: '',
        phone: '',
        email: '',
        address: '',
        city: '',
        state: '',
        zipcode: ''
    });

    const [error, setError] = useState({
        firstname: '',
        lastname: '',
        phone: '',
        email: '',
        address: '',
        city: '',
        state: '',
        zipcode: ''
    });

    const [paymentMethod, setPaymentMethod] = useState('cash'); // State for selected payment method

    const handleInput = (e) => {
        const { name, value } = e.target;
        setCheckoutInput(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handlePaymentMethodChange = (e) => {
        setPaymentMethod(e.target.value);
    };

    const submitOrder = (e) => {
        // Handle order submission based on selected payment method
    };

    return (
        <section>
            <div className="row">
                <div className="col-md-6">
                    {/* Cart Information Card */}
                </div>
                <div className="col-md-6">
                    <div className="card">
                        <div className="card-header">
                            <h4>Basic Information</h4>
                        </div>
                        <div className="card-body">
                            <form>
                                {/* Your form inputs */}

                                {/* Payment Method Radio Buttons */}
                                <div className='form-check'>
                                    <input
                                        className='form-check-input'
                                        type='radio'
                                        name='paymentMethod'
                                        value='cash'
                                        onChange={handlePaymentMethodChange}
                                        checked={paymentMethod === 'cash'}
                                    />
                                    <label className='form-check-label'>
                                        Cash on Delivery
                                    </label>
                                </div>
                                <div className='form-check'>
                                    <input
                                        className='form-check-input'
                                        type='radio'
                                        name='paymentMethod'
                                        value='online'
                                        onChange={handlePaymentMethodChange}
                                        checked={paymentMethod === 'online'}
                                    />
                                    <label className='form-check-label'>
                                        Online Payment
                                    </label>
                                </div>

                                {/* Submit Button */}
                                <div className="col-md-12">
                                    <div className="form-group text-end">
                                        <button type="button" className="btn btn-primary mx-1" onClick={submitOrder}>Place Order</button>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Checkout;
